import { useEffect, useState } from 'react'
import { ethers } from 'ethers'

const Domain = ({ domain, ethDaddy, provider, id }) => {

  const buyHandler = async () => {
    const signer = await provider.getSigner()
    // let deployer, owner1
    // [deployer, owner1] = await provider.getSigners()
    const transaction = await ethDaddy.connect(signer).mint(id, {value: domain.cost})
    await transaction.wait()
  }

  return (
    <div className='card'>
      <div className='card__info'>
        <h3>
          {domain.isOwned==true} ? (
            <del>{domain.name}</del>
          ) : (
            <>{domain.name}</>
          )
        </h3>

        <p>
          <>
            <strong>
              {ethers.utils.formatEther(domain.cost.toString(), 'ether')}
            </strong>
            ETH
          </>
        </p>
      </div>

      <button
        type='button'
        className='card__button'
        onClick={() => buyHandler()}
      >
        Buy it
      </button>
    </div>
  );
}

export default Domain;
